export const memberList = function() {
    return [{
        date: '2016-05-02',
        name: '王小虎',
        address: '上海市普陀区金沙江路 1518 弄'
      }, {
        date: '2016-05-04',
        name: '王小虎',
        address: '上海市普陀区金沙江路 1517 弄'
      }, {
        date: '2016-05-01',
        name: '王小虎',
        address: '上海市普陀区金沙江路 1519 弄'
      }, {
        date: '2016-05-03',
        name: '王小虎',
        address: '上海市普陀区金沙江路 1516 弄'
      }];
}

var form = {
    userName:'',
    userPassword:'',
    userConfirmedPassword:'',
    userAccount:'',
    userPhone:'',
    userAge:'',
    userSex:'',
    userAddress:'',
}

export const dataForm = function() {
    return form;
}

export const setForm = function(row) {
    form = row;
}

