        //   }else{
        //     callback();
        //   }
        // })
}

export const dataRule = function(validatePass) {
    return {
        userName: [
            { required: true, message: '请输入用户姓名', trigger: 'blur' },
            { min: 2, max: 10, message: '长度在2至10个字符之间', trigger: 'blur' },
        ],

        userPassword: [
            { required: true, message: '请输入密码', trigger: 'blur' },
            { min: 6, max: 10, message: '长度在6至15个字符之间', trigger: 'blur' }
        ],

        userConfirmedPassword: [
            { validator: validatePass, trigger: 'blur' }
        ],

        userAccount: [
            { required: true, message: '请输入账户', trigger: 'blur' },
            { min: 3, max: 10, message: '长度在3至10个字符之间', trigger: 'blur' },
            // { validator: checkDuplicate, trigger: 'blur' }
        ],

        userPhone: [
            { required: true, message: '请输入电话', trigger: 'blur' },
            { min: 11, max: 11, message: '长度为11位', trigger: 'blur' },
        ],

        userAge: [
            { required: true, message: '请输入年龄', trigger: 'blur' },
        ],

        userSex:[
        { required: true, message: '请选择性别', trigger: 'change' },
        ],

        userAddress: [
            { required: true, message: '请输入住址', trigger: 'blur' },
        ],
    };
}

import request from '../utils/request';

export const selectAll = query => {
    return request({
        url: "selectAll",
        method:'get',
        params: query,
    })
}

export const selectAllMember = query=>{
    return request({
        url:"selectUser",
        method:'get',
        params:query
    });
};

export const addMember = query=>{
    return request({
        url:"addMember",
        method:'post',
        params:query
    });
};

export const modifyMember = query=>{
    return request({
        url:"modifyMember",
        method:'post',
        params:query
    });
};

export const deleteMember = query=>{
    return request({
        url:"deleteMember",
        method:'post',
        params:query
    });
};
