export const carList = function() {
    return [{
        id:'165askda',
        name:'测试车辆1',
        state:'待出车',
        description: '测试车辆'
      }]
}

export const stateList = function() {
    return [
        {
          value:'待出车',
          label:'待出车'
        },
        {
          value:'出车中',
          label:'出车中'
        },
      ]
}

const getCheckDuplicate = function(form) {
    return (rule,value,callback) => {
        if(form.id){
          return callback();
        }
        checkCar({id: value}).then(res=>{
        //   if(res.code == 200){
        //     callback(new Error('Account already exist'));
        //   }else{
        //     callback();
        //   }
            console.log(res);
            return callback();
        })
    }
}

export const carRules = function(form) {
    return {
        id: [
            { required: true, message: '请输入车辆编号', trigger: 'blur' },
            { min: 2, max: 10, message: '长度在3至10个字符之间', trigger: 'blur' },
            { validator: getCheckDuplicate(form), trigger: 'blur' }
        ],

        name: [
            { required: true, message: '请输入车辆名称', trigger: 'blur' },
        ],

        state:[
        { required: true, message: '请选择车辆状态', trigger: 'change' },
        ],

        description: [
            { required: true, message: '请输入车辆描述', trigger: 'blur' },
        ],
    }
}

export const carForm = function() {
    return {
        id:'',
        name:'',
        state:'',
        description:''
    }
}

import request from '../utils/request';

export const checkCar = query=>{
    return request({
        url:"findCarById",
        method:'get',
        params:query
    })
}

export const selectAllCar = query=>{
    return request({
        url:"selectAllCar",
        method:'get',
        params:query
    });
};

export const addCar = query=>{
    return request({
        url:"addCar",
        method:'post',
        params:query
    });
};

export const modifyCar = query=>{
    return request({
        url:"modifyCar",
        method:'post',
        params:query
    });
};

export const deleteCar = query=>{
    return request({
        url:"deleteCar",
        method:'post',
        params:query
    });
};
